import React from "react";
import { Row, Col, Button } from "react-bootstrap"; // react bootstrap is a frontend framework for templates
import { LinkContainer } from "react-router-bootstrap"; // using react router to nagivate pages
import "./Home.css";

function Home() {
    return (
        <Row>
            <Col md={6} className="d-flex flex-direction-column align-items-center justify-content-center p-3 mb-2 bg-warning text-dark">
                <div>
                    <h1>Welcome to Dialogue!</h1>
                    <p>Dialogue is our MERN stack project based on a messaging app. We've developed group chats with rooms to talk about your passions and hobbies.</p>
                    <LinkContainer to="/login"> 
                        <Button variant="success">
                            Let's have a Dialogue! <i className="fas fa-comments home-message-icon"></i>
                        </Button>
                    </LinkContainer>
                </div>
            </Col>
            <Col md={6} className="home__bg"></Col>
        </Row>
    );
}

export default Home;
